import React, {useState, useEffect} from 'react'
import axios from 'axios'
import Zimmer from './requirements/zimmer'
import '../index.css'
import BuchenPopup from './buchenPopup'


let openButton = undefined
const popupSetUp =     <div className="buchenStyle" id="popUp-Container">
<div className="buchen-main">
    <h1>Buchung Sitzungszimmer</h1>
    <h2>Test Zimmername</h2>
    <h2>Test Zimmerstandort</h2>
</div>
<button id="closePopup" className="closePopupButton">Close</button>
</div>
const closeButton = document.getElementById('closePopup')
const popUp = document.getElementById('popUp-Container')

const port = 5001;
const SuchAusgabe = () => {
  const [standOrte, setStandOrte] = useState([])
  const [sitzungsZimmer, setSitzungsZimmer] = useState([])

  useEffect(() => {
    axios.get(`http://localhost:${port}/standorte/`).then((response) => {
     setStandOrte(response.data)
    })
  }, [])
  useEffect(() => {
    axios.get(`http://localhost:${port}/sitzungszimmer/`).then((response) => {
      setSitzungsZimmer(response.data)   
    })
  }, [])


  return (<div className='Such-Ausgabe'>
    <div className='standort-test'>

      <div className='sitzungsZimmer'>{sitzungsZimmer.map( zimmer =>  <Zimmer zimmername={zimmer.zimmerName} standort={zimmer.standortName} stockwerk={zimmer.stockwerk} maxP={zimmer.maxPersonen} />
      )}
      </div>
      <button id="openPopup" className='popUpButton'>Open Buchung</button>
      {openButton = document.getElementById('openPopup')}
      {popupSetUp}
      
    </div>
  </div>
  )
};


openButton.addEventListener('click', async () => {
  popUp.classList.add('show')
})

closeButton.addEventListener('click', async () => {
  popUp.classList.remove('show')
})


console.log(Zimmer)

console.log(Zimmer)

export default SuchAusgabe;
