import "../index.css"
import React, {useState, useEffect} from 'react'


const BuchenControler = () => {
  return (
    <div className="buchenStyle" id="popUp-Container">
    <div className="buchen-main">
      <h1>Buchung Sitzungszimmer</h1>
      <h2>Test Zimmername</h2>
      <h2>Test Zimmerstandort</h2>
      
      </div>
      <button id="closePopup" className="closePopupButton">Close</button>
    </div>
  )
}

const openButton = document.getElementById('openPopup')
const closeButton = document.getElementById('closePopup')
const popUp = document.getElementById('popUp-Container')

openButton.addEventListener('click', () => {
  popUp.classList.add('show')
})

closeButton.addEventListener('click', () => {
  popUp.classList.remove('show')
})
export default BuchenControler 
  


