import React from 'react'
import '../index.css'
import BuchenPopup from './buchenPopupContent'


const BuchenLogic = ({ showModal}) => {

    function testModal() {
        if (showModal == true) {
            return <div className='test2'><BuchenPopup /></div>
        }if(showModal == false){
            const popUpDiv = document.getElementById("test2");
            popUpDiv.classList.add("testing");
          }
        }
        return <>{testModal()} </>
    }
    


export default BuchenLogic
